htmlform.script:
jQuery(function() {
jQuery( '.toggle-button' ).on( 'click', function() {
// À décommenter si tu veux masquer toutes les autres div
jQuery( '.togglable' ).not( this.getAttribute( 'data-target' ) ).hide( 'slow'
);

jQuery( this.getAttribute( 'data-target' ) ).toggle( 'slow' );
});
});

//Valition des donnees

	beforeSubmit.push(function() {

		if(getValue('travailleur_sexe_b.value')==1065 || getValue('travailleur_sexe_b.value')==1066){
			setValue('travailleur_sexe_a.value', '160580');
		}
		if(getValue('echange_sexe_b.value')==1065 || getValue('echange_sexe_b.value')==1066){
			setValue('echange_sexe_a.value', '160579');
		}
		if(getValue('form_status.value') != 163339 && getValue('form_status.value') != 1267)
		{
			alert('Erreur ! Veuillez saisir le Statut de la fiche !');
		}
		return true;
	});

jQuery(function() {

	jQuery('.titre').each(function() {
		var m =jQuery(this).find(".master").attr("id");
		if(getValue(''+m+'.value')>0)
		{
			jQuery( this.getAttribute( 'ch-target' ) ).show();
		}
		else{
			if(getValue(''+m+'.value') == null){
				jQuery( this.getAttribute( 'ch-target' ) ).hide();
			}
		}
		if(getValue(''+m+'.value') == ''){
				jQuery( this.getAttribute( 'ch-target' ) ).hide();
		}
	});

	jQuery( '.titre' ).on( 'change', function() {

		var m =jQuery(this).find(".master").attr("id");
		if(getValue(''+m+'.value')>0 || getValue(''+m+'.value') != null)
		{
			jQuery( this.getAttribute( 'ch-target' ) ).show();
		}
		else{
			if(getValue(''+m+'.value') == null){
				jQuery( this.getAttribute( 'ch-target' ) ).hide();
			}

		}
		if(getValue(''+m+'.value') == ''){
				jQuery( this.getAttribute( 'ch-target' ) ).hide();
		}


	});

});
